if ( !function_exists( 'aioseop_output_notice' ) ) {
	function aioseop_output_notice( $message, $id = '', $class = "updated fade" ) {
		$class = 'aioseop_notice ' . $class;
		if ( !empty( $class ) )	$class = ' class="' . esc_attr( $class ) . '"';
		if ( !empty( $id ) )	$class .= ' id="' . esc_attr( $id ) . '"';
