			if ( isset( $_POST["count_role_$role"] ) && $_POST["count_role_$role"] )
				$options['count_roles'][] = $role;

		stats_set_options( $options );
		stats_update_blog();
		Jetpack::state( 'message', 'module_configured' );
		wp_safe_redirect( Jetpack::module_configuration_url( 'stats' ) );
		exit;
	}
}

